import { useContext } from "react";
import Context from "../Context/Context";

const PharmacyList = ({ list }) => {
  const ctx = useContext(Context);
  const addCartHandler = async (e) => {
    try {
      const response = await fetch(
        "https://crudcrud.com/api/271c214b36ee44bdae24dea0da743e64/pharmacy"
      );
      const data = await response.json();
      const index = data.findIndex(
        (item) => Number(item.id) === Number(e.target.id)
      );
      const med = data[index];
      ctx.addItems(med);
      fetch(
        "https://crudcrud.com/api/244d6adb122347e492a24335983922b6/cartitems",
        {
          method: "POST",
          headers: {
            "Content-Type": "application/json ; charset=utf-8",
          },
          mode: "no-cors",
          body: JSON.stringify({
            id: med.id,
            name: med.name,
            description: med.description,
            quantity: 1,
          }),
        }
      );
    } catch (error) {
      console.log(error);
    }
  };
  return (
    <>
      {list.map((item) => {
        return (
          <tr key={item.id}>
            <td>{item.name}</td>
            <td>{item.description}</td>
            <td>{item.price}</td>
            <td>
              <button id={item.id} onClick={addCartHandler}>
                Add to Cart
              </button>
            </td>
          </tr>
        );
      })}
    </>
  );
};
export default PharmacyList;
