import { useContext } from "react";
import Context from "../Context/Context";

const PharmacyList = ({ list }) => {
  const ctx = useContext(Context);
  const addCartHandler = async (e) => {
    try {
      const response = await fetch(
        "https://crudcrud.com/api/271c214b36ee44bdae24dea0da743e64/pharmacy"
      );
      const data = await response.json();
      const index = data.findIndex(
        (item) => Number(item.id) === Number(e.target.id)
      );
      const med = data[index];
      ctx.addItems(med);
    } catch (error) {
      console.log(error);
    }

    try {
      const response1 = await fetch(
        "https://crudcrud.com/api/271c214b36ee44bdae24dea0da743e64/pharmacy"
      );
      const data = await response1.json();
      const index = data.findIndex(
        (item) => Number(item.id) === Number(e.target.id)
      );
      const med = data[index];

      const response = await fetch(
        "https://crudcrud.com/api/3a5d1bff1d8f415da679c68f93966abc/cartitems",
        {
          method: "POST",
          headers: {
            "Content-Type": "application/json",
          },
          body: JSON.stringify({ med }),
        }
      );
      if (response.ok) {
        console.log("success");
      }
    } catch (error) {
      console.log(error);
    }
  };
  return (
    <>
      {list.map((item) => {
        return (
          <tr key={item.id}>
            <td>{item.name}</td>
            <td>{item.description}</td>
            <td>{item.price}</td>
            <td>
              <button id={item.id} onClick={addCartHandler}>
                Add to Cart
              </button>
            </td>
          </tr>
        );
      })}
    </>
  );
};
export default PharmacyList;
