import React, { useReducer } from "react";
const Context = React.createContext({ items: [], addItems: (item) => {} });

const defaultCartState = {
  items: [],
};
const cartReducer = async (state, action) => {
  if (action.type === "add") {
    let updatedItems;
    updatedItems = [
      ...state.items,
      {
        id: action.item.id,
        name: action.item.name,
        description: action.item.price,
        quantity: 1,
      },
    ];
    console.log(updatedItems);
    try {
      const response = await fetch(
        "https://crudcrud.com/api/244d6adb122347e492a24335983922b6/cartitems",
        {
          method: "POST",
          headers: {
            "Content-Type": "application/json",
          },
          mode: "no-cors",
          body: JSON.stringify(updatedItems),
        }
      );
      if (response.ok) {
        console.log("success");
      }
    } catch (error) {
      console.log(error);
    }
    return {
      items: updatedItems,
    };
  }
};
export const ContextProvider = (props) => {
  const [cartState, dispenseCartAction] = useReducer(
    cartReducer,
    defaultCartState
  );
  const addItemHandler = (med) => {
    dispenseCartAction({ type: "add", item: med });
  };
  const meds = {
    items: cartState.items,
    addItems: addItemHandler,
  };
  return <Context.Provider value={meds}>{props.children}</Context.Provider>;
};

export default Context;
